import React, { useState, useEffect } from "react";
import Faq from "react-faq-component";

// import sections
import Hero from "../components/sections/Hero";
import FeaturesTiles from "../components/sections/FeaturesTiles";
import FeaturesSplit from "../components/sections/FeaturesSplit";
import Testimonial from "../components/sections/Testimonial";
import MeetTheTeam from "../components/sections/MeetTheTeam";
// import sections

const data = {
	title: "faq",
	rows: [
		{
			title: "Why 11,111 items?",
			content:
				"It is a tribute to the insulin patent, which was sold for only $1. The inventors sold it for $1 to a university because they knew people needed it to stay alive, and they believed profiting off of this “life juice” would be unethical. Today it is sold for hundreds of dollars per vial. ",
		},
		{
			title: "Reveal Type? ",
			content:
				"Diadragons have a delayed reveal, which means if you buy on mint day, you will receive a dragon egg. This dragon egg will hatch into a random Diadragon. The beauty of this reveal type is that you have no idea how rare your diadragon will be. The rarest traits and most common traits will mint at the same price. ",
		},
		{
			title: "How do you guarantee that I will receive a random Diadragon? ",
			content:
				"We will use a provenance hash. This ensures that all eggs receive a random diadragon. No one will know which eggs are rare before they hatch. Not even the engineers behind the project.",
		},
		{
			title: "What blockchain is it on?",
			content: "ERC-721 token on the Ethereum Blockchain. ",
		},
		{
			title: "How much will it cost to mint?",
			content:
				"Each Diadragon will be minted for 0.08 Eth + Gas on October 14th at 6pm PST. You can earn early access and bulk discounts through our discord channel.",
		},
		{
			title: "What value will I get besides owning digital art?",
			content:
				"The team will continuously add to the value we provide but for now there are guaranteed plans of gamification, health related giveaways (personal chefs, private trainers, vacations etc) ",
		},
		{
			title: "Can I still win a giveaway if I want to remain anonymous?",
			content:
				"Yes. All giveaway correspondence will happen over discord.  Upon request, the coordination of prizes can be handled by the third party professional and  the winner. The team/community will never know your name, address, or other personal information. ",
		},
	],
};

const [rows, setRowsOption] = useState(null);

useEffect(() => {
	if (rows) {
		setTimeout(() => {
			rows[0].expand();
		}, 2500);

		setTimeout(() => {
			rows[0].close();
		}, 5000);

		setTimeout(() => {
			rows[0].scrollIntoView();
			// rows[0].scrollIntoView(true);
		}, 10000);
	}
}, [rows]);

const styles = {
	bgColor: "#00000000",
	titleTextColor: "white",
	rowTitleColor: "white",
	rowContentPaddingBottom: "10px",
	rowContentPaddingLeft: "10px",
	rowContentPaddingRight: "10px",
	rowContentColor: "white",
	arrowColor: "white",
};

const config = {
	// animate: true,
	// arrowIcon: "V",
	// tabFocus: true
};

const FAQ = () => {
	return (
		<>
			<div className='faqs'>
				<Faq
					data={data}
					styles={styles}
					config={config}
					getRowOptions={setRowsOption}
				/>
			</div>
		</>
	);
};

export default FAQ;
