import React, { Component } from "react";
import ReactDOM from "react-dom";
import Faq from "react-faq-component";
import Header from "../components/layout/Header";
import Footer from "../components/layout/Footer";

// import sections

const data = {
	title: "faq",
	rows: [
		{
			title: "Why 11,111 items?",
			content:
				"It is a tribute to the insulin patent, which was sold for only $1. The inventors sold it for $1 to a university because they knew people needed it to stay alive, and they believed profiting off of this “life juice” would be unethical. Today it is sold for hundreds of dollars per vial. ",
		},
		{
			title: "Reveal Type? ",
			content:
				"Diadragons have a delayed reveal, which means if you buy on mint day, you will receive a dragon egg. This dragon egg will hatch into a random Diadragon. The beauty of this reveal type is that you have no idea how rare your diadragon will be. The rarest traits and most common traits will mint at the same price. ",
		},
		{
			title: "How do you guarantee that I will receive a random Diadragon? ",
			content: "We will use a provenance hash. This ensures that all eggs receive a random diadragon. No one will know which eggs are rare before they hatch. Not even the engineers behind the project.",
		},
		{
			title: "What blockchain is it on?",
			content:
				"ERC-721 token on the Ethereum Blockchain. ",
		},
		{
			title: "How much will it cost to mint?",
			content:
				"illo.mx is currently in public aplha and much about it is subject to change. please do not use it for more than testing and amusement for now.",
		},
		{
			title: "what's tao?",
			content: "tao is a smaller EVM compatible blockchain.",
		},
		{
			title: "what is illo.mx?",
			content:
				"illo.mx is a nft marketplace dapp running on the tao blockchain.",
		},
		{
			title: "why all the dust?",
			content:
				"illo.mx is currently in public aplha and much about it is subject to change. please do not use it for more than testing and amusement for now.",
		},
		{
			title: "what's tao?",
			content: "tao is a smaller EVM compatible blockchain.",
		},
	],
};

const styles = {
	bgColor: "#00000000",
	titleTextColor: "white",
	rowTitleColor: "white",
	rowContentPaddingBottom: "10px",
	rowContentPaddingLeft: "10px",
	rowContentPaddingRight: "10px",
	rowContentColor: "white",
	arrowColor: "white",
};

const config = {
	// animate: true,
	// arrowIcon: "V",
	// tabFocus: true
};

const FAQ = () => {
	return (
		<>
			<Header navPosition='right' className='reveal-from-bottom' />
			<div className='faqs'>
				<Faq data={data} styles={styles} config={config} />
			</div>
			<Footer className='footer-placement' />
		</>
	);
};

export default FAQ;
