import React from "react";
import classNames from "classnames";
import { SectionSplitProps } from "../../utils/SectionProps";
import SectionHeader from "./partials/SectionHeader";
import Image from "../elements/Image";
import image1 from "./../../assets/images/features-split-image-01.png";
import image2 from "./../../assets/images/features-split-image-02.png";
import image3 from "./../../assets/images/features-split-image-03.png";

const propTypes = {
	...SectionSplitProps.types,
};

const defaultProps = {
	...SectionSplitProps.defaults,
};

const FeaturesSplit = ({
	className,
	topOuterDivider,
	bottomOuterDivider,
	topDivider,
	bottomDivider,
	hasBgColor,
	invertColor,
	invertMobile,
	invertDesktop,
	alignTop,
	imageFill,
	...props
}) => {
	const outerClasses = classNames(
		"features-split section",
		topOuterDivider && "has-top-divider",
		bottomOuterDivider && "has-bottom-divider",
		hasBgColor && "has-bg-color",
		invertColor && "invert-color",
		className
	);

	const innerClasses = classNames(
		"features-split-inner section-inner",
		topDivider && "has-top-divider",
		bottomDivider && "has-bottom-divider"
	);

	const splitClasses = classNames(
		"split-wrap",
		invertMobile && "invert-mobile",
		invertDesktop && "invert-desktop",
		alignTop && "align-top"
	);

	const sectionHeader = {
		title: "What are Diadragons?",
		paragraph:
			"Diadragons are adorable dragons who dedicate their lives to keeping humans healthy and happy. Like a puppy, but less fluffy. They are one-of-a-kind NFTs that live on the Ethereum blockchain.",
	};

	return (
		<section {...props} className={outerClasses}>
			<div className='container'>
				<div className={innerClasses}>
					<SectionHeader data={sectionHeader} className='center-content' />
					<div className={splitClasses}>
						<div className='split-item'>
							<div
								className='split-item-content center-content-mobile reveal-from-left'
								data-reveal-container='.split-item'>
								<h3 className='mt-0 mb-12'>Why should I adopt a Diadragon? </h3>
								<p className='m-0'>
									To join the first NFT community built to address the insulin
									affordability crisis. <br></br>
									<br></br>In doing so, you’ll be eligible to win cool prizes,
									participate in our social media campaigns, and support our
									larger mission.<br></br>
									<br></br> Our primary goal is to utilize web-3 technology to
									enable anyone to donate funds directly to people who pay for
									insulin and to verify that the money is spent on insulin.{" "}
									<br></br>
									<br></br>Learn more about our mission
									<a href='https://medium.com/@lil_egg_meg/the-story-of-diadragons-an-nft-collection-against-life-tax-b5f8ca794a41'>
										{" "}
										here
									</a>
									<br></br>
									<br></br>
									Updates and progress will be posted in our{" "}
									<a href='https://discord.com/invite/hVYBgdKQVq'>
										discord channel
									</a>
									.
								</p>
							</div>
							<div
								className={classNames(
									"split-item-image center-content-mobile reveal-from-bottom",
									imageFill && "split-item-image-fill"
								)}
								data-reveal-container='.split-item'>
								<Image
									src={image3}
									alt='Features split 01'
									width={528}
									height={396}
								/>
							</div>
						</div>

						<div className='split-item'>
							<div
								className='split-item-content center-content-mobile reveal-from-right'
								data-reveal-container='.split-item'>

								<h3 className='mt-0 mb-12'>Why this team? </h3>
								<p className='m-0'>
									Thanasi Dilos and the community at Civics Unplugged are a
									group of developers capable of building the secure platforms
									that will make direct donations for insulin a reality.
									<br></br>
									<br></br>
									Meghan Sharkus, a Thiel Fellow, and 6-year entrepreneur in the
									diabetes space is ready to utilize her connections to
									integrate the solution into existing insulin purchasing
									channels. She has funded the development of this project
									completely out-of-pocket and is committed to making this
									vision a reality.
									<br></br>
									<br></br>
									The team of artists, animators, developers, and marketers is
									willing to try, fail, and try again until the project is a
									success. We originally launched on Ethereum and when the
									platform became oversaturated and fees were over $150 per
									dragon, we rebuilt the project on Polygon and relaunched in a
									more affordable and accessible way.
								</p>
							</div>
							<div
								className={classNames(
									"split-item-image center-content-mobile reveal-from-bottom",
									imageFill && "split-item-image-fill"
								)}
								data-reveal-container='.split-item'>
								<Image
									src={image2}
									alt='Features split 02'
									width={528}
									height={396}
								/>
							</div>
						</div>

						<div className='split-item'>
							<div
								className='split-item-content center-content-mobile reveal-from-left'
								data-reveal-container='.split-item'>
								<h3 className='mt-0 mb-12'>Weekly Prize Drops</h3>
								<p className='m-0'>
									A portion of NFT sales will go towards YOU. We will be
									conducting giveaways every single week with a focus on health
									and life experience. Always wanted a personal trainer but
									never made the commitment? Your Diadragon will bring you one.
									Wanted to take a trip to Europe but haven’t prioritized it?
									Your Diadragon may have the whole thing already planned out
									for you.
								</p>
							</div>
							<div
								className={classNames(
									"split-item-image center-content-mobile reveal-from-bottom",
									imageFill && "split-item-image-fill"
								)}
								data-reveal-container='.split-item'>
								<Image
									src={image1}
									alt='Features split 03'
									width={528}
									height={396}
								/>
							</div>
						</div>
					</div>
				</div>
			</div>
		</section>
	);
};

FeaturesSplit.propTypes = propTypes;
FeaturesSplit.defaultProps = defaultProps;

export default FeaturesSplit;
