import React from "react";
// import sections
import Hero from "../components/sections/Hero";
import FeaturesTiles from "../components/sections/FeaturesTiles";
import FeaturesSplit from "../components/sections/FeaturesSplit";
import Testimonial from "../components/sections/Testimonial";
import MeetTheTeam from "../components/sections/MeetTheTeam";
import { Chrono } from "react-chrono";

const Home = () => {
	const items = [
		{
			title: "Prelaunch",
			cardTitle: "Daily Challenges to win a Free-Diadragon!\n5-question-Fridays on Kahoot! Winner gets a Diadragon! ",
			cardSubtitle:
				"Men of the British Expeditionary Force (BEF) wade out to..",
			cardDetailedText: "Men of the British Expeditionary Force (BEF) ",
		},
		{
			title: "May 1940",
			cardTitle: "Dunkirk",
			cardSubtitle:
				"Men of the British Expeditionary Force (BEF) wade out to..",
			cardDetailedText: "Men of the British Expeditionary Force (BEF) ",
		},
		{
			title: "May 1940",
			cardTitle: "Dunkirk",
			cardSubtitle:
				"Men of the British Expeditionary Force (BEF) wade out to..",
			cardDetailedText: "Men of the British Expeditionary Force (BEF) ",
		},
		{
			title: "May 1940",
			cardTitle: "Dunkirk",
			cardSubtitle:
				"Men of the British Expeditionary Force (BEF) wade out to..",
			cardDetailedText: "Men of the British Expeditionary Force (BEF) ",
		},
		{
			title: "May 1940",
			cardTitle: "Dunkirk",
			cardSubtitle:
				"Men of the British Expeditionary Force (BEF) wade out to..",
			cardDetailedText: "Men of the British Expeditionary Force (BEF) ",
		},
		{
			title: "May 1940",
			cardTitle: "Dunkirk",
			cardSubtitle:
				"Men of the British Expeditionary Force (BEF) wade out to..",
			cardDetailedText: "Men of the British Expeditionary Force (BEF) ",
		},
	];
	return (
		<>
			<Hero className='illustration-section-01' />
			<FeaturesSplit
				invertMobile
				topDivider
				imageFill
				className='illustration-section-02'
			/>
			{/* <FeaturesTiles /> */}
			<Chrono
				items={items}
				mode='VERTICAL_ALTERNATING'
				useReadMore={false}
				theme={{
					primary: "white",
					secondary: "transparent",
					cardBgColor: "transparent",
					cardForeColor: "white",
					titleColor: "white",
				}}
			/>
			<Testimonial topDivider />
			<MeetTheTeam />
		</>
	);
};

export default Home;
