import React from "react";
import classNames from "classnames";
import { SectionTilesProps } from "../../utils/SectionProps";
import SectionHeader from "./partials/SectionHeader";
import Image from "../elements/Image";
import image1 from "./../../assets/images/feature-tile-icon-01.svg";
import image2 from "./../../assets/images/feature-tile-icon-02.svg";
import image3 from "./../../assets/images/feature-tile-icon-03.svg";
import image4 from "./../../assets/images/feature-tile-icon-04.svg";
import image5 from "./../../assets/images/feature-tile-icon-05.svg";
import image6 from "./../../assets/images/feature-tile-icon-06.svg";
import styles from "./../../App.css";

const propTypes = {
	...SectionTilesProps.types,
};

const defaultProps = {
	...SectionTilesProps.defaults,
};
const FeaturesTiles = ({
	className,
	topOuterDivider,
	bottomOuterDivider,
	topDivider,
	bottomDivider,
	hasBgColor,
	invertColor,
	pushLeft,
	...props
}) => {
	const outerClasses = classNames(
		"features-tiles section",
		topOuterDivider && "has-top-divider",
		bottomOuterDivider && "has-bottom-divider",
		hasBgColor && "has-bg-color",
		invertColor && "invert-color",
		className
	);

	const innerClasses = classNames(
		"features-tiles-inner section-inner pt-0",
		topDivider && "has-top-divider",
		bottomDivider && "has-bottom-divider"
	);

	const tilesClasses = classNames(
		"tiles-wrap center-content",
		pushLeft && "push-left"
	);

	const sectionHeader = {
		title: "Build up the whole picture",
		paragraph:
			"Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum â€” semper quis lectus nulla at volutpat diam ut venenatis.",
	};

	return (
		<section {...props} className={outerClasses}>
			<div className='container'>
				<div className={innerClasses}>
					<SectionHeader data={sectionHeader} className='center-content' />
					<div className={tilesClasses}>
						<div className='tiles-item reveal-from-bottom'>
							<div className='tiles-item-inner'>
								<div className='features-tiles-item-header'>
									<div className='features-tiles-item-image mb-16'>
										<img
											className={styles.testhover}
											src={image1}
											alt='Features tile icon 01'
											width={64}
											height={64}
										/>
									</div>
								</div>
								<div className='features-tiles-item-content'>
									<h4 className='mt-0 mb-8'>10% sold</h4>
									<p className='m-0 text-sm'>
										1 lucky Diadragon owner will be the first ever person to use
										or gift a Life Drop and receive an exclusive ultra rare
										Diadragon!
									</p>
								</div>
							</div>
						</div>

						<div
							className='tiles-item reveal-from-bottom'
							data-reveal-delay='200'>
							<div className='tiles-item-inner'>
								<div className='features-tiles-item-header'>
									<div className='features-tiles-item-image mb-16'>
										<Image
											src={image2}
											alt='Features tile icon 02'
											width={64}
											height={64}
										/>
									</div>
								</div>
								<div className='features-tiles-item-content'>
									<h4 className='mt-0 mb-8'>25% sold</h4>
									<p className='m-0 text-sm'>
										1 lucky Diadragon owner will be the first ever person to use
										or gift a Life Drop and receive an exclusive ultra rare
										Diadragon!
									</p>
								</div>
							</div>
						</div>

						<div
							className='tiles-item reveal-from-bottom'
							data-reveal-delay='400'>
							<div className='tiles-item-inner'>
								<div className='features-tiles-item-header'>
									<div className='features-tiles-item-image mb-16'>
										<Image
											src={image3}
											alt='Features tile icon 03'
											width={64}
											height={64}
										/>
									</div>
								</div>
								<div className='features-tiles-item-content'>
									<h4 className='mt-0 mb-8'>50% sold</h4>
									<p className='m-0 text-sm'>
										1 lucky Diadragon owner will be the first ever person to use
										or gift a Life Drop and receive an exclusive ultra rare
										Diadragon!
									</p>
								</div>
							</div>
						</div>

						<div className='tiles-item reveal-from-bottom'>
							<div className='tiles-item-inner'>
								<div className='features-tiles-item-header'>
									<div className='features-tiles-item-image mb-16'>
										<Image
											src={image4}
											alt='Features tile icon 04'
											width={64}
											height={64}
										/>
									</div>
								</div>
								<div className='features-tiles-item-content'>
									<h4 className='mt-0 mb-8'>75% sold</h4>
									<p className='m-0 text-sm'>
										1 lucky Diadragon owner will be the first ever person to use
										or gift a Life Drop and receive an exclusive ultra rare
										Diadragon!
									</p>
								</div>
							</div>
						</div>

						<div
							className='tiles-item reveal-from-bottom'
							data-reveal-delay='200'>
							<div className='tiles-item-inner'>
								<div className='features-tiles-item-header'>
									<div className='features-tiles-item-image mb-16'>
										<Image
											src={image5}
											alt='Features tile icon 05'
											width={64}
											height={64}
										/>
									</div>
								</div>
								<div className='features-tiles-item-content'>
									<h4 className='mt-0 mb-8'>100% sold</h4>
									<p className='m-0 text-sm'>
										1 lucky Diadragon owner will be the first ever person to use
										or gift a Life Drop and receive an exclusive ultra rare
										Diadragon!
									</p>
								</div>
							</div>
						</div>
					</div>
				</div>
			</div>
		</section>
	);
};

FeaturesTiles.propTypes = propTypes;
FeaturesTiles.defaultProps = defaultProps;

export default FeaturesTiles;
