import React, { useEffect } from "react";
import PropTypes from "prop-types";
import classNames from "classnames";

const propTypes = {
	children: PropTypes.node,
	handleClose: PropTypes.func.isRequired,
	show: PropTypes.bool.isRequired,
	closeHidden: PropTypes.bool,
	video: PropTypes.string,
	videoTag: PropTypes.oneOf(["iframe", "video"]),
};

const defaultProps = {
	children: null,
	show: false,
	closeHidden: false,
	video: "",
	videoTag: "iframe",
};

const Modal = ({
	className,
	children,
	handleClose,
	show,
	closeHidden,
	video,
	videoTag,
	...props
}) => {
	useEffect(() => {
		document.addEventListener("keydown", keyPress);
		document.addEventListener("click", stopProgagation);
		return () => {
			document.removeEventListener("keydown", keyPress);
			document.removeEventListener("click", stopProgagation);
		};
	});

	useEffect(() => {
		handleBodyClass();
	}, [props.show]);

	const handleBodyClass = () => {
		if (document.querySelectorAll(".modal.is-active").length) {
			document.body.classList.add("modal-is-active");
		} else {
			document.body.classList.remove("modal-is-active");
		}
	};

	const keyPress = (e) => {
		e.keyCode === 27 && handleClose(e);
	};

	const stopProgagation = (e) => {
		e.stopPropagation();
	};

	const classes = classNames(
		"modal",
		show && "is-active",
		video && "modal-video",
		className
	);

	return (
		<>
			{show && (
				<div {...props} className={classes} onClick={handleClose}>
					<div className='modal-inner mt-16' onClick={stopProgagation}>
						<div className='responsive-video'>
							<iframe
								width='853'
								height='480'
								src={`https://www.youtube.com/watch?v=0L68m7e7_9w&ab_channel=DiadragonsNFT`}
								frameBorder='0'
								allow='accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture'
								allowFullScreen
								title='Embedded youtube'
							/>
						</div>
					</div>
				</div>
			)}
		</>
	);
};

Modal.propTypes = propTypes;
Modal.defaultProps = defaultProps;

export default Modal;
